import React from 'react';
import '../styles/NewEventForm.css';
import { useState } from 'react';

export default function NewEventForm({ eventId }) {

    function pad(d) {
        return (d < 10) ? '0' + d.toString() : d.toString();
    }

    const today = new Date();
    const day = today.getDate();
    const year = today.getFullYear();
    const month = pad(today.getMonth() + 1);

    const [title, setTitle] = useState('');
    const [date, setDate] = useState(year + "-" + month + "-" + day);
    const [id, setId] = useState(eventId);


    const resetForm = () => {
        setTitle('');
        setDate('');
    }

    const handleSubmit = (e) => {

        e.preventDefault();



        const event = {
            title: title,
            date: date,
            id: id
        }

        const newId = parseInt(eventId) + 1;

        setId(newId);

        resetForm()
    }



    return (
        <form className='new-event-form' onSubmit={handleSubmit}>
            <label>
                <span>Event Title:</span>
                <input type="text" onChange={(e) => setTitle(e.target.value)} value={title}></input>
            </label>
            <label>
                <span>Event Date:</span>
                <input type="date" onChange={(e) => setDate(e.target.value)} value={date}></input>
            </label>
            <button>Submit</button>
        </form>
    )
}
